		<template template_name='edit' save_template_file='y' template_type='webpage' template_notes='' edit_date='1317938792' last_author_id='1' cache='n' refresh='0' no_auth_bounce='' enable_http_auth='n' allow_php='n' php_parse_location='o' hits='157'>
		<![CDATA[
{if logged_out}{redirect="profile/register"}{/if}
<!doctype html>
<head>
    <title>Edit Profile</title>
    <link href="{path=css/_ee_saef_css}" type="text/css" rel="stylesheet" media="screen" />
</head>
<body>

<p><a href="{exp:profile:logout_link}">Logout</a></p>

{exp:profile:edit return="profile"}
	<fieldset>
		<label>Username</label>
		<input type="text" name="username" value="{username}" />
    	<label>Screen Name</label>
		<input type="text" name="screen_name" value="{screen_name}" />
    	<label>Email</label>
		<input type="text" name="email" value="{email}" />
	</fieldset>
	<fieldset>
        <p>Leave blank if you do not wish to change it</p>
		<label>New Password</label>
		<input type="password" name="password" value="" />
		<label>Confirm New Password</label>
		<input type="password" name="password_confirm" value="" />
	{!-- superadmins don't have to submit existing password --}
	{if logged_in_group_id != 1}
		<label>Your Existing Password (You must submit the current password to update this page)</label>
		<input type="password" name="current_password" value="" />
	{/if}
	</fieldset>
	<fieldset>
		{custom_fields}
        <p>
			<label for="{field_name}">{if required}* {/if}{field_label}</label>

			{field_instructions}

			{formatting_buttons}

			{if textarea}
				<textarea id="{field_name}" name="{field_name}" dir="{text_direction}" rows="{rows}">{field_data}</textarea>
			{/if}

			{if text}
				<input type="text" dir="{text_direction}" id="{field_name}" name="{field_name}" value="{field_data}" maxlength="{maxlength}" size="50" />
			{/if}

			{if select}
				<select id="{field_name}" name="{field_name}">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}

			{if date}
				<input type="text" id="{field_name}" name="{field_name}" value="{field_data}" size="50" />
			{/if}

			{if checkbox}
				{options}<label class="checkbox">{option_value} <input type="checkbox" id="{field_name}" name="{field_name}[]" value="{option_value}"{checked} /></label>{/options}
			{/if}

			{if radio}
				{options}<label class="checkbox">{option_value}<label class="checkbox"> <input type="radio" id="{field_name}" name="{field_name}" value="{option_value}"{checked} /></label>{/options}
			{/if}

			{if file}
				{display_field}
			{/if}

			{!-- uncomment if you have safecracker file installed--}
			{!--
			{if safecracker_file}
				{display_field}
			{/if}
			--}

			{if relationship}
				<select id="{field_name}" name="{field_name}">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}

			{if multiselect}
				<select id="{field_name}" name="{field_name}[]" multiple="multiple">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}
        </p>
		{/custom_fields}
	</fieldset>
	<fieldset>
		<input type="submit" value="Save" />
	</fieldset>
{/exp:profile:edit}
</body>
</html>		]]>
		</template>
		<template template_name='register' save_template_file='y' template_type='webpage' template_notes='' edit_date='1317936325' last_author_id='1' cache='n' refresh='0' no_auth_bounce='' enable_http_auth='n' allow_php='y' php_parse_location='i' hits='88'>
		<![CDATA[
{if logged_in}{redirect='profile/edit'}{/if}
<html>
<head>
    <title>Register</title>
    <link href="{path=css/_ee_saef_css}" type="text/css" rel="stylesheet" media="screen" />
</head>
<body>

<section>
    <h1>Login</h1>
{exp:profile:login return="profile/edit"}
    <fieldset>
		<label>Username</label>
		<input type="text" name="username" value="">
		<label>Password</label>
		<input type="password" name="password" value="">
        {if auto_login}
        <label>Remember Me? <input type="checkbox" name="auto_login" value="1" checked="checked"></label>
        {/if}
        <input type="submit" value="Login">
	</fieldset>
{/exp:profile:login}
</section>

<section>
    <h1>Register</h1>
{exp:profile:register id="register" return="profile/edit"}
	<fieldset>
		<label>Username</label>
		<input type="text" name="username" value="{username}" />
		<label>Screen Name</label>
		<input type="text" name="screen_name" value="{screen_name}" />
	</fieldset>
	<fieldset>
		<label>Password</label>
		<input type="password" name="password" value="" />
		<label>Confirm Password</label>
		<input type="password" name="password_confirm" value="" />
	</fieldset>
	<fieldset>
		<label>Email</label>
		<input type="text" name="email" value="{email}" />
		<label>Confirm Email</label>
		<input type="text" name="email_confirm" value="{email_confirm}" />
	</fieldset>
	<fieldset>
		{custom_fields}
        <p>
			<label for="{field_name}">{if required}* {/if}{field_label}</label>

			{field_instructions}

			{formatting_buttons}

			{if textarea}
				<textarea id="{field_name}" name="{field_name}" dir="{text_direction}" rows="{rows}">{field_data}My bio.</textarea>
			{/if}

			{if text}
				<input type="text" dir="{text_direction}" id="{field_name}" name="{field_name}" value="{field_data}" maxlength="{maxlength}" size="50" />
			{/if}

			{if select}
				<select id="{field_name}" name="{field_name}">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}

			{if date}
				<input type="text" id="{field_name}" name="{field_name}" value="{field_data}" size="50" />
			{/if}

			{if checkbox}
				{options}<label class="checkbox">{option_value} <input type="checkbox" id="{field_name}" name="{field_name}[]" value="{option_value}"{checked} /></label>{/options}
			{/if}

			{if radio}
				{options}<label class="checkbox">{option_value}<label class="checkbox"> <input type="radio" id="{field_name}" name="{field_name}" value="{option_value}"{checked} /></label>{/options}
			{/if}

			{if file}
				{display_field}
			{/if}

			{!-- uncomment if you have safecracker file installed--}
			{!--
			{if safecracker_file}
				{display_field}
			{/if}
			--}

			{if relationship}
				<select id="{field_name}" name="{field_name}">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}

			{if multiselect}
				<select id="{field_name}" name="{field_name}[]" multiple="multiple">
				{options}<option value="{option_value}"{selected}>{option_name}</option>{/options}
				</select>
			{/if}
        </p>
		{/custom_fields}
	</fieldset>
    {if captcha}
    <fieldset>
    <p>Please enter the word you see in the image below:</p>
    <p>{captcha}<br />
    <input type="text" name="captcha" value="" /></p>
    </fieldset>
    {/if}
	<fieldset>
		<input type="submit" value="Register" />
	</fieldset>
{/exp:profile:register}
</section>
</body>
</html>		]]>
		</template>
